            cor_2f4_2f5 = round(cor(Q2F4_,Q2F5_, use = "complete.obs"), digits = 2),
            # Learning self-determination (Q2C4_) & Self-advocacy (Q3A1_)
            cor_2c4_3a1 = round(cor(Q2C4_,Q3A1_, use = "complete.obs"), digits = 2),
